#!perl
($X,$y,$i)=(121,'b'x11,0);sub z{($s,$x,$m)=@_;$m+=f($x++,$s)while($x<$X);$m;}sub f{my($n,$t,$k)=@_;$c=substr($t,$n,1);return 0 if$c eq' '||!$k&&$c ne'w';return 1 if$c eq'b';substr($t,$n,1)='b';(!(($n+1)%11)||f($n+1,$t,1))*(!(($n-1)%10)||f($n-1,$t,1))*f($n+11,$t,1)*f($n-11,$t,1);}$y.="b$_"while(<>);$y=~s/\n/b/g;$d=z($y.='b'x11);for(;$i++<$X;){$h=substr($y,$i,1);if($h eq' '){substr($b=$y,$i,1)='b';if(z($b)>$d){$v=int($i/11);$w=$i-($v*11);print"$v $w\n";}}}
