#!perl
($X,$y,$i)=(111,'b'x10,0);$l=$y;sub z{($s,$m)=@_;for(0..$X){$m+=f($_,$s)}$m;}sub f{my($n,$t,$k)=@_;$c=substr($t,$n,1);' 'eq$c||!$k&&'w'ne$c?0:'b'eq$c||!(substr($t,$n,1)='b')?1:f($n+1,$t,1)*f($n-1,$t,1)*f($n+10,$t,1)*f($n-10,$t,1);}while(<>){chomp;$y.="b$_"}$d=z($y.=$l);for(;$i++<$X;){$h=substr($y,$i,1);if(' 'eq$h){substr($b=$y,$i,1)='b';if(z($b)>$d){$v=int$i/10;$w=$i-$v*10;print"$v $w\n";}}}
